mapper.select:
	/* Query ID : adver.mng.selectAdverMngListVO */
	SELECT

	FROM ADVER_MNG
	WHERE 1=1

  	ORDER BY adver_no DESC
  	LIMIT #{startNum}, #{endNumMysql}

/* Query ID : adver.mng.selectAdverMng */
SELECT

FROM	ADVER_MNG
WHERE  adver_no = #{adverNo}

mapper.insert:
/* Query ID : adver.mng.insertAdverMng */
INSERT INTO ADVER_MNG (
	adver_nm
	, use_yn
	, adver_area
	, link_ty
	, link_url
	, bgng_dt
	, end_dt
	, reg_unique_id
	, reg_dt
	, reg_id
	, rgtr
	) VALUES (
	#{adverNm, jdbcType=VARCHAR}
	, #{useYn, jdbcType=VARCHAR}
	, #{adverArea, jdbcType=VARCHAR}
	, #{linkTy, jdbcType=VARCHAR}
	, #{linkUrl, jdbcType=VARCHAR}
	, #{bgngDt}
	, #{endDt}
	, #{regUniqueId, jdbcType=VARCHAR}
	, SYSDATE()
	, #{regId, jdbcType=VARCHAR}
	, #{rgtr, jdbcType=VARCHAR}
	)

	SELECT LAST_INSERT_ID()

mapper.update:
/* Query ID :
adverMng.updateAdverMng */
UPDATE ADVER_MNG SET
adver_nm = #{adverNm, jdbcType=VARCHAR}
, use_yn = #{useYn, jdbcType=VARCHAR}
, adver_area = #{adverArea, jdbcType=VARCHAR}
, link_ty = #{linkTy, jdbcType=VARCHAR}
, link_url = #{linkUrl, jdbcType=VARCHAR}
, bgng_dt = #{bgngDt}
, end_dt = #{endDt}
, mdfcn_unique_id = #{mdfcnUniqueId, jdbcType=VARCHAR}
, mdfcn_dt = SYSDATE()
, mdfcn_id = #{mdfcnId, jdbcType=VARCHAR}
, mdfr = #{mdfr, jdbcType=VARCHAR}
WHERE adver_no = #{adverNo}
