mapper.select:
SELECT
FROM MNG_MAIN WHERE 1=1
ORDER BY thema_ty, sort_no DESC LIMIT #{startNum}, #{endNumMysql}

mapper.update:
UPDATE MNG_MAIN SET
	use_yn = #{useYn}
WHERE main_no = #{mainNo}

UPDATE MNG_MAIN SET
	sort_no = #{sortNo}
WHERE main_no = #{mainNo}

mapper.insert:
INSERT INTO MNG_MAIN (
	sort_no
	, thema_ty
	, sj
	, rdcnt
	, link_url
	, use_yn
	, reg_unique_id
	, reg_dt
	, reg_id
	, rgtr
) VALUES (
	#{sortNo}
	, #{themaTy, jdbcType=VARCHAR}
	, #{sj, jdbcType=VARCHAR}
	, #{rdcnt}
	, #{linkUrl, jdbcType=VARCHAR}
	, #{useYn, jdbcType=VARCHAR}
	, #{regUniqueId, jdbcType=VARCHAR}
	, SYSDATE()
	, #{regId, jdbcType=VARCHAR}
	, #{rgtr, jdbcType=VARCHAR}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE MNG_MAIN SET
	sort_no = #{sortNo}
	, thema_ty = #{themaTy}
	, sj = #{sj}
	, rdcnt = #{rdcnt}
	, link_url = #{linkUrl}
	, use_yn = #{useYn}
	, mdfcn_unique_id = #{mdfcnUniqueId}
	, mdfcn_dt = #{mdfcnDt}
	, mdfcn_id = #{mdfcnId}
	, mdfr = #{mdfr}
WHERE main_no = #{mainNo}
