mapper.select:
SELECT
FROM MBR_AGREEMENT ma WHERE 1=1
ORDER BY ma.reg_dt DESC LIMIT 1;

mapper.insert:
INSERT INTO MBR_AGREEMENT(
	MBR_UNIQUE_ID
	, TERMS_YN
	, TERMS_DT
	, PRIVACY_YN
	, PRIVACY_DT
	, PROVISION_YN
	, PROVISION_DT
	, THIRD_PARTIES_YN
	, THIRD_PARTIES_DT
	, REG_DT
)VALUES(
	#{mbrUniqueId}
	, #{termsYn}
	, #{termsDt}
	, #{privacyYn}
	, #{privacyDt}
	, #{provisionYn}
	, #{provisionDt}
	, #{thirdPartiesYn}
	, #{thirdPartiesDt}
	, SYSDATE()
)
