mapper.select:
SELECT
FROM CART c WHERE 1=1
ORDER BY cart_no ASC LIMIT #{startNum}, #{endNumMysql}

SELECT entrps_dlvygrp_no , entrps_no , entrps_dlvygrp_nm , dlvy_adit_amt , dlvy_calc_ty , use_yn
FROM entrps_dlvygrp WHERE use_yn ='Y' AND ENTRPS_DLVYGRP_NO in ( SELECT DISTINCT IFNULL(g.ENTRPS_DLVYGRP_NO, 0) ENTRPS_DLVYGRP_NO FROM cart c INNER JOIN gds g ON c.GDS_NO = g.GDS_NO WHERE g.DLVY_GROUP_YN = 'Y' AND recipter_unique_id = #{srchRecipterUniqueId}
AND view_yn = #{srchViewYn}
AND cart_grp_no IN
#{srchCartGrpNo}
)

SELECT entrps_no , entrps_nm , entrps_store_nm , brno , rprsv_nm , induty , bizcnd , zip , addr , daddr , telno , fxno , eml , tkcg_md , use_yn , dlvy_ct_cnd , dlvy_base_ct , clcln_cycle , bank_nm , actno , dpstr , ctrt_bgng_ymd , ctrt_end_ymd , ctrt_ymd , fee , pic_nm , pic_telno , pic_telno_hp , pic_eml , pic_job
FROM ENTRPS where ENTRPS_NO in ( SELECT DISTINCT IFNULL(g.ENTRPS_NO, 0) ENTRPS_NO FROM cart c INNER JOIN gds g ON c.GDS_NO = g.GDS_NO WHERE recipter_unique_id = #{srchRecipterUniqueId}
AND view_yn = #{srchViewYn}
AND cart_grp_no IN
#{srchCartGrpNo}
)

mapper.insert:
/* Query ID : cart.insertCart */
INSERT INTO CART (
	 cart_grp_no
	 , cart_ty

	 , gds_no
	 , gds_cd
	 , bnef_cd
	 , gds_nm
	 , gds_pc

	 , ordr_pc
	 , ordr_optn_ty
	 , ordr_optn
	 , ordr_optn_pc
	 , ordr_qy
	 , gds_optn_no

	, view_yn

	 , recipter_unique_id
	 , bplc_unique_id

	 , reg_unique_id
	 , reg_dt
	 , reg_id
	 , rgtr
) VALUES (

		FN_NEXT_AUTO_SEQ('CART')
		#{cartGrpNo}

	 , #{cartTy}

	 , #{gdsNo}
	 , #{gdsCd, jdbcType=VARCHAR}
	 , #{bnefCd, jdbcType=VARCHAR}
	 , #{gdsNm, jdbcType=VARCHAR}
	 , #{gdsPc}

	 , #{ordrPc}
	 , #{ordrOptnTy, jdbcType=VARCHAR}
	 , #{ordrOptn, jdbcType=VARCHAR}
	 , #{ordrOptnPc}
	 , #{ordrQy}
	 , #{gdsOptnNo}

	 , #{viewYn}

	 , #{recipterUniqueId, jdbcType=VARCHAR}
	 , #{bplcUniqueId, jdbcType=VARCHAR}

	 , #{regUniqueId, jdbcType=VARCHAR}
	 , SYSDATE()
	 , #{regId, jdbcType=VARCHAR}
	 , #{rgtr, jdbcType=VARCHAR}
)

SELECT LAST_INSERT_ID()

mapper.update:
/* Query ID : cart.updateCartQy */
UPDATE CART
SET ordr_pc				= #{ordrPc}
	, ordr_qy			= #{ordrQy}
	, MDFCN_UNIQUE_ID 	= #{regUniqueId, jdbcType=VARCHAR}
	, MDFCN_DT 			= SYSDATE()
	, MDFCN_ID 			= #{regId, jdbcType=VARCHAR}
	, MDFR 				= #{rgtr, jdbcType=VARCHAR}
WHERE cart_no = #{cartNo} AND reg_unique_id = #{regUniqueId}

/* Query ID : cart.updateCartPc */
UPDATE CART
SET ordr_pc				= #{ordrPc}
	, gds_pc			= #{gdsPc}
	, ordr_optn_pc		= #{ordrOptnPc}
	, MDFCN_UNIQUE_ID 	= #{regUniqueId, jdbcType=VARCHAR}
	, MDFCN_DT 			= SYSDATE()
	, MDFCN_ID 			= #{regId, jdbcType=VARCHAR}
	, MDFR 				= #{rgtr, jdbcType=VARCHAR}
WHERE cart_no = #{cartNo} AND reg_unique_id = #{regUniqueId}

mapper.delete:
DELETE 
FROM CART
WHERE cart_no = #{srchCartNo} AND cart_grp_no = #{srchCartGrpNo} 
	AND gds_optn_no = #{gdsOptnNo} AND recipter_unique_id = #{srchRecipterUniqueId}


			AND cart_ty IN ('R', 'L')

				AND cart_ty = #{srchCartTy}

mapper.insert:
/* mergeOptnChg */
INSERT INTO CART (
	cart_no
	, cart_grp_no
	, cart_ty
	, gds_no
