	, ordr_qy
	, gds_optn_no
	, recipter_unique_id
	, bplc_unique_id
	, reg_unique_id
	, reg_dt
	, reg_id
	, rgtr
) VALUES (
	#{cartNo}
	, #{cartGrpNo}
	, #{cartTy, jdbcType=VARCHAR}
	, #{gdsNo}
	, #{gdsCd, jdbcType=VARCHAR}
	, #{bnefCd, jdbcType=VARCHAR}
	, #{gdsNm, jdbcType=VARCHAR}
	, #{gdsPc}
