mapper.select:
/* Query ID : mbrPoint.selectMbrPointListVO */ SELECT
, mng.mngr_memo FROM MBR_POINT p LEFT JOIN POINT_MNG mng ON p.point_mng_no = mng.point_mng_no WHERE 1=1
ORDER BY p.point_no DESC LIMIT #{startNum}, #{endNumMysql}

/* Query ID : mbrPoint.selectMbrPoint */ SELECT
FROM MBR_POINT p WHERE 1=1
ORDER BY point_no DESC LIMIT 1

/* Query ID : pointMng.selectPointMng */ SELECT
, mng.mngr_memo FROM MBR_POINT p LEFT JOIN POINT_MNG mng ON p.point_mng_no = mng.point_mng_no WHERE 1=1
ORDER BY point_no DESC

mapper.insert:
/* Query ID : mbrPoint.insertMbrPoint */
INSERT INTO MBR_POINT (
	point_mng_no
	, unique_id
	, ordr_cd
	, ordr_dtl_cd
	, point_se
	, point_cn
	, point
	, point_acmtl
	, give_mthd
	, reg_unique_id
	, reg_dt
	, reg_id
	, rgtr
) VALUES (
	#{pointMngNo}
	, #{uniqueId, jdbcType=VARCHAR}
	, #{ordrCd, jdbcType=VARCHAR}
	, #{ordrDtlCd, jdbcType=VARCHAR}
	, #{pointSe, jdbcType=VARCHAR}
	, #{pointCn, jdbcType=VARCHAR}
	, #{point}

	, IFNULL((SELECT point_acmtl FROM MBR_POINT mp WHERE mp.unique_id = #{uniqueId} ORDER BY point_no DESC LIMIT 1), 0) + #{point}


	, IFNULL((SELECT point_acmtl FROM MBR_POINT mp WHERE mp.unique_id = #{uniqueId} ORDER BY point_no DESC LIMIT 1), 0) - #{point}

	, #{giveMthd, jdbcType=VARCHAR}
	, #{regUniqueId, jdbcType=VARCHAR}
	, SYSDATE()
	, #{regId, jdbcType=VARCHAR}
	, #{rgtr, jdbcType=VARCHAR}
	)
