mapper.select:
SELECT ntt_no AS up_no , file_ty , file_no , flpth , orgnl_file_nm , strg_file_nm , file_extn , file_sz , file_dc , dwnld_cnt , reg_dt , use_yn FROM BBS_ATCHFILE WHERE ntt_no = #{upNo}
AND file_ty = #{fileTy}
AND use_yn = 'Y' ORDER BY ntt_no, file_no

SELECT ntt_no AS up_no , file_ty , file_no , flpth , orgnl_file_nm , strg_file_nm , file_extn , file_sz , file_dc , dwnld_cnt , reg_dt , use_yn FROM BBS_ATCHFILE WHERE ntt_no = #{upNo}
AND file_ty = #{fileTy}
AND file_no = #{fileNo}
AND use_yn = 'Y'

mapper.insert:

			SELECT IFNULL(MAX(file_no), 0) + 1
			FROM BBS_ATCHFILE WHERE ntt_no = #{upNo} AND file_ty = #{fileTy}

		INSERT INTO BBS_ATCHFILE (
			ntt_no
			, file_ty
			, file_no
			, flpth
			, orgnl_file_nm
			, strg_file_nm
			, file_extn
			, file_sz
			, file_dc
			, reg_dt
			, dwnld_cnt
			, use_yn
		) VALUES (
			#{upNo}
			, #{fileTy}

            , #{maxFileNo}


            , #{fileNo}

			, #{flpth}
			, #{orgnlFileNm}
			, #{strgFileNm}
			, #{fileExtsn}
			, #{fileSz}
			, #{fileDc, jdbcType=VARCHAR}
			, SYSDATE()
			, #{dwnldCnt}
			, #{useYn}
		)

mapper.update:
UPDATE BBS_ATCHFILE SET
	dwnld_cnt = dwnld_cnt + 1
WHERE ntt_no = #{upNo}

	AND file_ty = #{fileTy}

	AND file_no = #{fileNo}

UPDATE BBS_ATCHFILE SET
	file_dc = #{fileDc}
WHERE ntt_no = #{upNo}

	AND file_ty = #{fileTy}

	AND file_no = #{fileNo}

UPDATE BBS_ATCHFILE SET
	use_yn = 'N'
 WHERE ntt_no = #{upNo}

	AND file_ty = #{fileTy}

	AND file_no IN

		#{no}

UPDATE BBS_ATCHFILE SET
	use_yn = 'N'
 WHERE ntt_no = #{upNo}

mapper.delete:
DELETE FROM BBS_ATCHFILE
WHERE ntt_no = #{upNo}
	AND file_no = #{fileNo}

	AND file_ty = #{fileTy}
