mapper.select:
/* Query ID : mng.menu.selectMngMenuList */ WITH RECURSIVE re_mng_menu AS ( ( SELECT menu_no, up_menu_no, menu_nm, menu_url, icon, menu_ty, 1 as level_no, sort_no, use_yn FROM MNG_MENU WHERE up_menu_no = 0 ) UNION ALL ( SELECT p.menu_no, p.up_menu_no, p.menu_nm, p.menu_url, p.icon, p.menu_ty, (1 + re_mng_menu.level_no) as level_no, p.sort_no, p.use_yn FROM MNG_MENU AS p INNER JOIN re_mng_menu ON p.up_menu_no = re_mng_menu.menu_no ) ) SELECT menu_no , up_menu_no , menu_nm , menu_url , icon , menu_ty , level_no , sort_no , use_yn , (SELECT COUNT(menu_no) FROM MNG_MENU A WHERE A.up_menu_no = re_mng_menu.menu_no) AS child_cnt FROM re_mng_menu
use_yn = #{useYn}
ORDER BY up_menu_no, level_no, sort_no

/* Query ID : mng.menu.selectMngMenuAuthList */ WITH RECURSIVE re_mng_menu AS ( ( SELECT menu_no, up_menu_no, menu_nm, menu_url, icon, menu_ty, 1 as level_no, sort_no, use_yn , CONCAT('/', menu_no) as menu_path , CONCAT('/', menu_nm) as menu_nm_path FROM MNG_MENU WHERE up_menu_no = 0 )
UNION ALL ( SELECT p.menu_no, p.up_menu_no, p.menu_nm, p.menu_url, p.icon, p.menu_ty, (1 + re_mng_menu.level_no) as level_no, p.sort_no, p.use_yn , CONCAT(re_mng_menu.menu_path, '/', p.menu_no) AS menu_path , CONCAT(re_mng_menu.menu_nm_path, '/', p.menu_nm) AS menu_nm_path FROM MNG_MENU AS p INNER JOIN re_mng_menu ON p.up_menu_no = re_mng_menu.menu_no ) ) SELECT re_mng_menu.menu_no , re_mng_menu.up_menu_no , re_mng_menu.menu_nm , re_mng_menu.menu_url , re_mng_menu.icon , re_mng_menu.menu_ty , re_mng_menu.level_no , re_mng_menu.sort_no , re_mng_menu.use_yn , IFNULL(tmam.authrt_yn, 'N') AS authrt_yn , IFNULL(tmam.inq_yn, 'N') AS inq_yn , IFNULL(tmam.wrt_yn, 'N') AS wrt_yn , menu_path , menu_nm_path , (SELECT COUNT(menu_no) FROM MNG_MENU A WHERE A.up_menu_no = re_mng_menu.menu_no) AS child_cnt FROM re_mng_menu LEFT OUTER JOIN MNG_AUTHRT_MENU tmam ON re_mng_menu.menu_no = tmam.menu_no AND tmam.authrt_no = #{authrtNo} WHERE re_mng_menu.use_yn = 'Y'
AND tmam.inq_yn = #{inqYn}
AND tmam.authrt_yn = #{authrtYn}
ORDER BY up_menu_no, level_no, sort_no

mapper.insert:
/* Query ID : sysmng.menu.insertMngMenu */
INSERT INTO MNG_MENU (
	up_menu_no
	, menu_nm
	, menu_url
	, icon

	, menu_ty

	, level_no
	, sort_no
	, use_yn
) VALUES (
	#{upMenuNo}
	, #{menuNm, jdbcType=VARCHAR}
	, #{menuUrl, jdbcType=VARCHAR}
	, #{icon, jdbcType=VARCHAR}

	, #{menuTy}

	, #{levelNo}
	, #{sortNo}
	, #{useYn}
)

	SELECT LAST_INSERT_ID()

mapper.update:
/* Query ID : sysmng.menu.updateMngMenuPosition */
UPDATE MNG_MENU


       sort_no = #{sortNo},


       up_menu_no = ${upMenuNo},


 WHERE menu_no = #{menuNo}

/* Query ID : sysmng.menu.updateMngMenu */
UPDATE MNG_MENU
	SET up_menu_no = #{upMenuNo}
		, menu_nm = #{menuNm}
		, menu_url = #{menuUrl, jdbcType=VARCHAR}

		, menu_ty = #{menuTy}

		, icon = #{icon, jdbcType=VARCHAR}
		, level_no = #{levelNo}
		, sort_no = #{sortNo}
		, use_yn = #{useYn}
WHERE menu_no = #{menuNo}

/* Query ID : sysmng.menu.updateMngMenuNm */
UPDATE MNG_MENU
   SET menu_nm = #{menuNm}
 WHERE menu_no = #{menuNo}

mapper.delete:
/* Query ID : sysmng.menu.deleteMngMenu */
DELETE FROM MNG_MENU
WHERE menu_no = #{menuNo}
AND menu_ty IN ('3', '4')

mapper.select:
	/* Query ID : sysmng.menu.selectLowerMenuCheck */
	SELECT COUNT(*)
	  FROM MNG_MENU
	WHERE menu_no IN
	(
	   SELECT menu_no
	   	 FROM MNG_MENU
    CONNECT BY PRIOR menu_no = up_menu_no
	   	START WITH up_menu_no = #{menuNo}
	)

mapper.update:
/* Query ID : sysmng.menu.updateLowerMenuuseYn */
UPDATE MNG_MENU
SET use_yn = #{useYn}
WHERE menu_no IN
(
  SELECT menu_no
  FROM MNG_MENU
    CONNECT BY PRIOR menu_no = up_menu_no
    START WITH up_menu_no = #{menuNo}
)

/* Query ID : sysmng.menu.updateMenuuseYn */
UPDATE MNG_MENU
SET use_yn = #{useYn}
WHERE menu_no = #{menuNo}
