mapper.select:
SELECT
FROM MNGR_EXCEL_LOG WHERE 1=1
ORDER BY reg_dt DESC LIMIT #{startNum}, #{endNumMysql}

mapper.insert:
 INSERT INTO MNGR_EXCEL_LOG (
 	unique_id
	, mngr_id
	, mngr_nm
	, url
	, dmnd_ip
	, use_hist
	, resn
	, reg_dt
  ) VALUES (
        #{uniqueId, jdbcType=VARCHAR}
       , #{mngrId, jdbcType=VARCHAR}
       , #{mngrNm, jdbcType=VARCHAR}
       , #{url, jdbcType=VARCHAR}
       , #{dmndIp, jdbcType=VARCHAR}
       , #{useHist, jdbcType=VARCHAR}
       , #{resn, jdbcType=VARCHAR}
	   , SYSDATE()
  )

	SELECT LAST_INSERT_ID()
